import asyncio

from web3 import Web3

from networks import Network, Arbitrum


class Contract:
    def __init__(
            self,
            chain: Network,
            address: str,
            abi: str | dict | list
    ) -> None:

        self.chain = chain
        self.address = Web3.to_checksum_address(address)
        self.abi = abi
        self.contract = chain.provider.eth.contract(address=self.address, abi=self.abi)

    def __str__(self) -> str:
        return f"{self.chain}: {self.address}"


class Token(Contract):
    def __init__(
            self,
            chain: Network,
            address: str,
            abi: str | dict | list,
            decimals: int = None,
            symbol: str | None = None,
            custom: dict | bool = False
    ) -> None:

        super().__init__(chain, address, abi)

        if not custom:
            self.decimals = decimals if decimals else asyncio.run(self.contract.functions.decimals().call())
            self.symbol = symbol if symbol else asyncio.run(self.contract.functions.symbol().call())

        else:
            self.custom = custom
            self.symbol = self.custom["symbol"] if "symbol" in custom else ""

    def __str__(self) -> str:
        return f"{self.chain} {self.symbol}"


